import React, { useState } from 'react';
import './App.css';

// Icon components
function BadgeAlertIcon(props) {
  return (
    <svg
      {...props}
      xmlns="http://www.w3.org/2000/svg"
      width="24"
      height="24"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    >
      <path d="M3.85 8.62a4 4 0 0 1 4.78-4.77 4 4 0 0 1 6.74 0 4 4 0 0 1 4.78 4.78 4 4 0 0 1 0 6.74 4 4 0 0 1-4.77 4.78 4 4 0 0 1-6.75 0 4 4 0 0 1-4.78-4.77 4 4 0 0 1 0-6.76Z" />
      <line x1="12" x2="12" y1="8" y2="12" />
      <line x1="12" x2="12.01" y1="16" y2="16" />
    </svg>
  );
}

function CheckIcon(props) {
  return (
    <svg
      {...props}
      xmlns="http://www.w3.org/2000/svg"
      width="24"
      height="24"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    >
      <path d="M20 6 9 17l-5-5" />
    </svg>
  );
}

function ClockIcon(props) {
  return (
    <svg
      {...props}
      xmlns="http://www.w3.org/2000/svg"
      width="24"
      height="24"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    >
      <circle cx="12" cy="12" r="10" />
      <polyline points="12 6 12 12 16 14" />
    </svg>
  );
}

function DoorOpenIcon(props) {
  return (
    <svg
      {...props}
      xmlns="http://www.w3.org/2000/svg"
      width="24"
      height="24"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    >
      <path d="M13 4h3a2 2 0 0 1 2 2v14" />
      <path d="M2 20h3" />
      <path d="M13 20h9" />
      <path d="M10 12v.01" />
      <path d="M13 4.562v16.157a1 1 0 0 1-1.242.97L5 20V5.562a2 2 0 0 1 1.515-1.94l4-1A2 2 0 0 1 13 4.561Z" />
    </svg>
  );
}

function FastForwardIcon(props) {
  return (
    <svg
      {...props}
      xmlns="http://www.w3.org/2000/svg"
      width="24"
      height="24"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    >
      <polygon points="13 19 22 12 13 5 13 19" />
      <polygon points="2 19 11 12 2 5 2 19" />
    </svg>
  );
}

function LightbulbIcon(props) {
  return (
    <svg
      {...props}
      xmlns="http://www.w3.org/2000/svg"
      width="24"
      height="24"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    >
      <path d="M15 14c.2-1 .7-1.7 1.5-2.5 1-.9 1.5-2.2 1.5-3.5A6 6 0 0 0 6 8c0 1 .2 2.2 1.5 3.5.7.7 1.3 1.5 1.5 2.5" />
      <path d="M9 18h6" />
      <path d="M10 22h4" />
    </svg>
  );
}

function MoonIcon(props) {
  return (
    <svg
      {...props}
      xmlns="http://www.w3.org/2000/svg"
      width="24"
      height="24"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    >
      <path d="M12 3a6 6 0 0 0 9 9 9 9 0 1 1-9-9Z" />
    </svg>
  );
}

function RefreshCwIcon(props) {
  return (
    <svg
      {...props}
      xmlns="http://www.w3.org/2000/svg"
      width="24"
      height="24"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    >
      <path d="M3 12a9 9 0 0 1 9-9 9.75 9.75 0 0 1 6.74 2.74L21 8" />
      <path d="M21 3v5h-5" />
      <path d="M21 12a9 9 0 0 1-9 9 9.75 9.75 0 0 1-6.74-2.74L3 16" />
      <path d="M8 16H3v5" />
    </svg>
  );
}

function SettingsIcon(props) {
  return (
    <svg
      {...props}
      xmlns="http://www.w3.org/2000/svg"
      width="24"
      height="24"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    >
      <path d="M12.22 2h-.44a2 2 0 0 0-2 2v.18a2 2 0 0 1-1 1.73l-.43.25a2 2 0 0 1-2 0l-.15-.08a2 2 0 0 0-2.73.73l-.22.38a2 2 0 0 0 .73 2.73l.15.1a2 2 0 0 1 1 1.72v.51a2 2 0 0 1-1 1.74l-.15.09a2 2 0 0 0-.73 2.73l.22.38a2 2 0 0 0 2.73.73l.15-.08a2 2 0 0 1 2 0l.43.25a2 2 0 0 1 1 1.73V20a2 2 0 0 0 2 2h.44a2 2 0 0 0 2-2v-.18a2 2 0 0 1 1-1.73l.43-.25a2 2 0 0 1 2 0l.15.08a2 2 0 0 0 2.73-.73l.22-.39a2 2 0 0 0-.73-2.73l-.15-.08a2 2 0 0 1-1-1.74v-.5a2 2 0 0 1 1-1.74l.15-.09a2 2 0 0 0 .73-2.73l-.22-.38a2 2 0 0 0-2.73-.73l-.15.08a2 2 0 0 1-2 0l-.43-.25a2 2 0 0 1-1-1.73V4a2 2 0 0 0-2-2zm-.22 7a3 3 0 1 0 3 3 3 3 0 0 0-3-3z" />
    </svg>
  );
}

function SunIcon(props) {
  return (
    <svg
      {...props}
      xmlns="http://www.w3.org/2000/svg"
      width="24"
      height="24"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    >
      <circle cx="12" cy="12" r="4" />
      <path d="M12 2v2" />
      <path d="M12 20v2" />
      <path d="m4.93 4.93 1.41 1.41" />
      <path d="m17.66 17.66 1.41 1.41" />
      <path d="M2 12h2" />
      <path d="M20 12h2" />
      <path d="m6.34 17.66-1.41 1.41" />
      <path d="m19.07 4.93-1.41 1.41" />
    </svg>
  );
}

function App() {
  const [lights, setLights] = useState({
    lobby: false,
    warehouse: false,
    exterior: false,
    offices: false
  });
  const [peopleCount, setPeopleCount] = useState(0);
  const [isConveyorMoving, setConveyorMoving] = useState(false);
  const [isGateOpen, setGateOpen] = useState(false);
  const [isAlarmActive, setAlarmActive] = useState(false);

  const toggleLight = (area) => {
    setLights(prevState => ({
      ...prevState,
      [area]: !prevState[area]
    }));
  };

  const toggleGate = () => {
    setGateOpen(prevState => !prevState);
  };

  const toggleAlarm = () => {
    setAlarmActive(prevState => !prevState);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Control de Establecimiento</h1>
      </header>
      <div className="container">
        <div className="control-container">
          <h2 className="control-title"><LightbulbIcon /> Control de Luces</h2>
          <div className="light-controls">
            <div>
              <label>Lobby</label>
              <div className="toggle-switch">
                <input
                  className="toggle-input"
                  id="lobby-toggle"
                  type="checkbox"
                  checked={lights.lobby}
                  onChange={() => toggleLight('lobby')}
                />
                <label className="toggle-label" htmlFor="lobby-toggle"></label>
              </div>
            </div>
            <div>
              <label>Warehouse</label>
              <div className="toggle-switch">
                <input
                  className="toggle-input"
                  id="warehouse-toggle"
                  type="checkbox"
                  checked={lights.warehouse}
                  onChange={() => toggleLight('warehouse')}
                />
                <label className="toggle-label" htmlFor="warehouse-toggle"></label>
              </div>
            </div>
            <div>
              <label>Offices</label>
              <div className="toggle-switch">
                <input
                  className="toggle-input"
                  id="offices-toggle"
                  type="checkbox"
                  checked={lights.offices}
                  onChange={() => toggleLight('offices')}
                />
                <label className="toggle-label" htmlFor="offices-toggle"></label>
              </div>
            </div>
            <div>
              <label>Exterior</label>
              <div className="toggle-switch">
                <input
                  className="toggle-input"
                  id="exterior-toggle"
                  type="checkbox"
                  checked={lights.exterior}
                  onChange={() => toggleLight('exterior')}
                />
                <label className="toggle-label" htmlFor="exterior-toggle"></label>
              </div>
            </div>
          </div>
        </div>
        <div className="control-container">
          <h2 className="control-title"><CheckIcon /> Conteo de Personas</h2>
          <h2>Total: {peopleCount}</h2>
        </div>
        <div className="control-container">
          <h2 className="control-title"><FastForwardIcon /> Banda Transportadora</h2>
          <div className="toggle-switch">
            <input
              className="toggle-input"
              id="conveyor-toggle"
              type="checkbox"
              checked={isConveyorMoving}
              onChange={() => setConveyorMoving(prevState => !prevState)}
            />
            <label className="toggle-label" htmlFor="conveyor-toggle"></label>
          </div>
          <p>‎ ‎ ‎ ‎ ‎ </p>
          <p>‎ ‎ ‎ ‎ ‎ </p>
          <p>Estado: {isConveyorMoving ? 'En Movimiento' : 'Detenida'}</p>
        </div>
        <div className="control-container">
          <h2 className="control-title"><DoorOpenIcon /> Control de Puerta</h2>
          <div className="door-controls">
            <label>‎ ‎ ‎ ‎ ‎</label>
            <label className="switch">
              <input type="checkbox" className="chk" checked={isGateOpen} onChange={toggleGate} />
              <span className="slider"></span>
            </label>
          </div>
          <p>‎ ‎ ‎ ‎ ‎ </p>
          <p>Estado: {isGateOpen ? 'Abierta' : 'Cerrada'}</p>
        </div>
        <div className="control-container">
          <h2 className="control-title"><BadgeAlertIcon /> Alarma Perimetral</h2>
          <button onClick={toggleAlarm}>
            {isAlarmActive ? 'Desactivar' : 'Activar'}
          </button>
          <p>Estado: {isAlarmActive ? 'Activada' : 'Desactivada'}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
