import React, { useState } from 'react';
import './App.css';

function App() {
  const [lights, setLights] = useState({
    lobby: false,
    warehouse: false,
    exterior: false,
    offices: false
  });
  const [peopleCount, setPeopleCount] = useState(0);
  const [isConveyorMoving, setConveyorMoving] = useState(false);
  const [isGateOpen, setGateOpen] = useState(false);
  const [isAlarmActive, setAlarmActive] = useState(false);

  const toggleLight = (area) => {
    setLights(prevState => ({
      ...prevState,
      [area]: !prevState[area]
    }));
  };

  const toggleGate = () => {
    setGateOpen(prevState => !prevState);
  };

  const toggleAlarm = () => {
    setAlarmActive(prevState => !prevState);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Control de Establecimiento</h1>
      </header>
      <div className="container">
        <div className="control-container">
          <h2>Control de Luces</h2>
          <div className="light-controls">
            <label>Lobby</label>
            <input type="checkbox" checked={lights.lobby} onChange={() => toggleLight('lobby')} />
            <label>Warehouse</label>
            <input type="checkbox" checked={lights.warehouse} onChange={() => toggleLight('warehouse')} />
            <label>Exterior</label>
            <input type="checkbox" checked={lights.exterior} onChange={() => toggleLight('exterior')} />
            <label>Offices</label>
            <input type="checkbox" checked={lights.offices} onChange={() => toggleLight('offices')} />
          </div>
        </div>
        <div className="control-container">
          <h2>Conteo de Personas</h2>
          <p>Total: {peopleCount}</p>
        </div>
        <div className="control-container">
          <h2>Banda Transportadora</h2>
          <button onClick={() => setConveyorMoving(prevState => !prevState)}>
            {isConveyorMoving ? "Detener" : "Iniciar"}
          </button>
          <p>Estado: {isConveyorMoving ? "En Movimiento" : "Detenida"}</p>
        </div>
        <div className="control-container">
          <h2>Control de Puerta</h2>
          <button onClick={toggleGate}>
            {isGateOpen ? "Cerrar" : "Abrir"}
          </button>
          <p>Estado: {isGateOpen ? "Abierta" : "Cerrada"}</p>
        </div>
        <div className="control-container">
          <h2>Alarma Perimetral</h2>
          <button onClick={toggleAlarm}>
            {isAlarmActive ? "Desactivar" : "Activar"}
          </button>
          <p>Estado: {isAlarmActive ? "Activada" : "Desactivada"}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
