import React, { useState } from 'react';
import './App.css';

function App() {
  // Estados para los controles de iluminación
  const [lighting, setLighting] = useState({
    lobby: false,
    warehouse: true,
    offices: false,
    exterior: true,
  });

  // Estado para el tráfico de clientes
  const [traffic, setTraffic] = useState({
    entries: 1234,
    exits: 987,
  });

  // Estado para la velocidad de la cinta transportadora
  const [conveyorSpeed, setConveyorSpeed] = useState(50);

  // Funciones para manejar cambios en los controles
  const toggleLight = (area) => {
    setLighting({ ...lighting, [area]: !lighting[area] });
  };

  // Renderizado de la interfaz
  return (
    <div className="App">
      <header className="App-header">
        {/* Controles de iluminación */}
        <div className="Lighting-controls">
          <ToggleSwitch label="Lobby" checked={lighting.lobby} onChange={() => toggleLight('lobby')} />
          {/* ...otros controles de iluminación */}
        </div>

        {/* Tráfico de clientes */}
        <div className="Customer-traffic">
          <Counter label="Entries" count={traffic.entries} />
          <Counter label="Exits" count={traffic.exits} />
        </div>

        {/* Cinta transportadora */}
        <div className="Conveyor-belt">
          <Slider value={conveyorSpeed} onChange={(e) => setConveyorSpeed(e.target.value)} />
        </div>

        {/* ...otros componentes como el control de puertas */}
      </header>
    </div>
  );
}

export default App;
