import React, { useState } from 'react';
import './App.css';

function App() {
  const [lights, setLights] = useState({
    lobby: false,
    warehouse: false,
    exterior: false,
    offices: false
  });
  const [peopleCount, setPeopleCount] = useState(0);
  const [isConveyorMoving, setConveyorMoving] = useState(false);
  const [isGateOpen, setGateOpen] = useState(false);
  const [isAlarmActive, setAlarmActive] = useState(false);

  const toggleLight = (area) => {
    setLights(prevState => ({
      ...prevState,
      [area]: !prevState[area]
    }));
  };

  const toggleGate = () => {
    setGateOpen(prevState => !prevState);
  };

  const toggleAlarm = () => {
    setAlarmActive(prevState => !prevState);
  };

  const toggleConveyor = () => {
    setConveyorMoving(prevState => !prevState);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Control de Establecimiento</h1>
      </header>
      <div className="container">
        <div className="control-container">
          <h2>Control de Luces</h2>
          <div className="light-controls">
            <div>
              <label>Lobby</label>
              <br />
              <div className="toggle-switch">
                <input
                  className="toggle-input"
                  id="lobby-toggle"
                  type="checkbox"
                  checked={lights.lobby}
                  onChange={() => toggleLight('lobby')}
                />
                <label className="toggle-label" htmlFor="lobby-toggle"></label>
              </div>
            </div>
            <div>
              <label>Warehouse</label>
              <br />
              <div className="toggle-switch">
                <input
                  className="toggle-input"
                  id="warehouse-toggle"
                  type="checkbox"
                  checked={lights.warehouse}
                  onChange={() => toggleLight('warehouse')}
                />
                <label className="toggle-label" htmlFor="warehouse-toggle"></label>
              </div>
            </div>
            <div>
              <label>Offices</label>
              <br />
              <div className="toggle-switch">
                <input
                  className="toggle-input"
                  id="offices-toggle"
                  type="checkbox"
                  checked={lights.offices}
                  onChange={() => toggleLight('offices')}
                />
                <label className="toggle-label" htmlFor="offices-toggle"></label>
              </div>
            </div>
            <div>
              <label>Exterior</label>
              <br />
              <div className="toggle-switch">
                <input
                  className="toggle-input"
                  id="exterior-toggle"
                  type="checkbox"
                  checked={lights.exterior}
                  onChange={() => toggleLight('exterior')}
                />
                <label className="toggle-label" htmlFor="exterior-toggle"></label>
              </div>
            </div>
          </div>
        </div>
        <div className="control-container">
          <h2>Conteo de Personas</h2>
          <p>Total: {peopleCount}</p>
        </div>
        <div className="control-container">
          <h2>Banda Transportadora</h2>
          <div className="toggle-switch">
            <input type="checkbox" id="checkbox" checked={isConveyorMoving} onChange={toggleConveyor} />
            <label className="switch" htmlFor="checkbox">
              Start
              <svg
                className="slider"
                viewBox="0 0 512 512"
                height="1em"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  d="M288 32c0-17.7-14.3-32-32-32s-32 14.3-32 32V256c0 17.7 14.3 32 32 32s32-14.3 32-32V32zM143.5 120.6c13.6-11.3 15.4-31.5 4.1-45.1s-31.5-15.4-45.1-4.1C49.7 115.4 16 181.8 16 256c0 132.5 107.5 240 240 240s240-107.5 240-240c0-74.2-33.8-140.6-86.6-184.6c-13.6-11.3-33.8-9.4-45.1 4.1s-9.4 33.8 4.1 45.1c38.9 32.3 63.5 81 63.5 135.4c0 97.2-78.8 176-176 176s-176-78.8-176-176c0-54.4 24.7-103.1 63.5-135.4z"
                ></path>
              </svg>
            </label>
          </div>
          <p>Estado: {isConveyorMoving ? 'En Movimiento' : 'Detenida'}</p>
        </div>
        <div className="control-container">
          <h2>Control de Puerta</h2>
          <label className="switch">
            <input type="checkbox" className="chk" checked={isGateOpen} onChange={toggleGate} />
            <span className="slider"></span>
          </label>
          <p>Estado: {isGateOpen ? 'Abierta' : 'Cerrada'}</p>
        </div>
        <div className="control-container">
          <h2>Alarma Perimetral</h2>
          <button onClick={toggleAlarm}>
            {isAlarmActive ? 'Desactivar' : 'Activar'}
          </button>
          <p>Estado: {isAlarmActive ? 'Activada' : 'Desactivada'}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
